const mongoose = require("mongoose");

const transactionSchema = new mongoose.Schema({
    userId: { type: String, ref: "User", required: true },
    transactionID: { type: String, required: true },
    base_amount: { type: Number },
    amount: { type: Number },
    mobile: { type: String, required: true },
    gateway_Number: { type: String },

    gateway_name: {
        type: String,
        enum: ["Bkash", "Nagad", "Rocket", "Upay", "transfer"],
        required: true,
    },

    type: {
        type: Number,
        enum: [0, 1], // 0 = Deposit, 1 = Withdrawal
        required: true,
    },
    status: {
        type: Number,
        enum: [0, 1, 2], // 0 = Hold, 1 = Accept, 2 = Reject
        required: true,
    },

    details: { type: String },

    payment_type: {
        type: String,
        enum: ["sendMoney", "cashout", "payment", "transfer"],
    },

    datetime: { type: Date, default: Date.now },
    is_commission: { type: Boolean, default: false },
    referredbyCode: { type: String },
    updatetime: { type: Date, default: Date.now },
});


transactionSchema.virtual("gateway_image_url").get(function () {
    const images = {
        Bkash: "data:image/png;base64,",
        Nagad: "data:image/png;base64,",
        Rocket: "data:image/png;base64,",
        Upay: "data:image/png;base64,",
        transfer: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQndsv0kYQ49g9nVlQzTUljJ2UcID-CHJn0CQ&s",
    };
    return images[this.gateway_name] || null;
});

// Enable virtuals in JSON and Object output
transactionSchema.set("toObject", { virtuals: true });
transactionSchema.set("toJSON", { virtuals: true });
module.exports = mongoose.model("Transaction", transactionSchema);
